		/* STOP WATCH LOGIC  */
		var Stopwatch;
		if (!Stopwatch) {Stopwatch = {};}
		function Stopwatch(displayTime){this.runtime = 0; this.timer = null; this.displayTime = displayTime; }
		Stopwatch.INCREMENT = 200;
		Stopwatch.prototype.doDisplay = function(){	if (!this.laptime) { this.displayTime(this.runtime);} 	else {	this.displayTime(this.laptime);	}}
		Stopwatch.prototype.startStop = function(){	if (!this.timer) {var instance = this;	this.timer = window.setInterval(function(){instance.runtime += Stopwatch.INCREMENT;	instance.doDisplay();}, Stopwatch.INCREMENT);} else {window.clearInterval(this.timer);	this.timer = null;	this.doDisplay();}}

		var _stopWatch = new Stopwatch(function(runtime) {
			// format time as m:ss.d
			var minutes = Math.floor(runtime / 60000);
			var seconds = Math.floor(runtime % 60000 / 1000);
			var decimals = Math.floor(runtime % 1000 / 100);
			var displayText = minutes + ":" + (seconds < 10 ? "0" : "") + seconds + "." + decimals;
			$("#stopWatch").html(displayText);
		});
	</script>	
	<script type="text/javascript">
		getNewURL = function() {
			$("#nurl").val(window.location.href.replace('install.php', ''));
		}
		
		validateInstall = function () {
			$('#dbconn-test-msg').html("Attempting Connection.  Please wait...");
			$('#dbconn-test').css('display', 'block');
			var dbmake 	= $('input#dbmake').is(':checked')  ? 1 : 0;
			var dbclean = $('input#dbclean').is(':checked') ? 1 : 0;
			
			var q;
			q  = "dbhost=" 		+  $("input#dbhost").val() + "&";
			q += "dbname=" 		+  $("input#dbname").val() + "&";
			q += "dbuser=" 		+  $("input#dbuser").val() + "&";
			q += "dbpassword="  +  $("input#dbpassword").val() + "&";
			q += "dbmake="  	+  dbmake + "&";
			q += "dbclean="  	+  dbclean;
			$.ajax({
				type: "POST",
				url: window.location.href,
				data: q + "&action=dbconnect-test",
				success: function(data){ $('#dbconn-test-msg').html(data); },
				error:   function(data){ alert('An error occurred while testing the database connection!')}
			});
